import { useState } from 'react';

const cards = [
  { src: '/public/assest/basketball.png', matched: false },
  { src: '/public/assest/closed_book.png', matched: false },
  { src: '/public/assest/comet.png', matched: false },
  { src: '/public/assest/cookie.png', matched: false },
  { src: '/public/assest/cover.png', matched: false },
  { src: '/public/assest/dizzy_face.png', matched: false },
  { src: '/public/assest/gear.png', matched: false },
  { src: '/public/assest/grapes.png', matched: false },
  { src: '/public/assest/tent.png', matched: false },
  { src: '/public/assest/tv.png', matched: false },
];

function App() {
  const [datacard, setDatacard] = useState([]);
  const [turns, setTurns] = useState(0);
  const [choseOne, setChoseOne] = useState(null);
  const [choseTwo, setChoseTwo] = useState(null);
  const [disabled, setDisabled] = useState(false);

  const shuffleCards = () => {
    const shuffled = [...cards, ...cards].sort(() => Math.random() - 0.5).map((card) => ({ ...card, id: Math.random() }));
    setTurns(0);
    setDatacard(shuffled);
  };

  shuffleCards();
  return <div className=""></div>;
}

export default App;
